const response = $input.first().json.data;
const html = response.body || response;

const match = html.match(/https:\/\/web\.whatsapp\.com\/\?v=([\d.-\w]+)/);

if (match && match[1]) {
  const version = match[1].replace(/-.*$/, ''); 
  return [{ json: { whatsappVersion: version } }];
} else {
  return [{ json: { error: "Versão não encontrada" } }];
}

const originalYaml = $('Stack').first().json.suastack;

const stackConvert = originalYaml
  .replace(/\\/g, '\\\\')
  .replace(/"/g, '\\"')
  .replace(/\n/g, '\\n')
  .replace(/\t/g, '\\t');

return [{
  json: {
    stackConvert: stackConvert
  }
}];
