// Extrai os dados do trigger do Telegram
// O objeto $json pode ser sÃ³ $json ou $json.message dependendo do evento
const message = $json.message;
const firstName = message.from.first_name || 'Cliente';
const chatId = message.chat.id;

// FunÃ§Ã£o para determinar a saudaÃ§Ã£o
function determinarSaudacao() {
    // Fuso horÃ¡rio do Brasil (-3)
    const agora = new Date(new Date().toLocaleString("en-US", {timeZone: "America/Sao_Paulo"}));
    const hora = agora.getHours();

    if (hora >= 6 && hora < 12) {
        return "Bom dia!";
    } else if (hora >= 12 && hora < 18) {
        return "Boa tarde!";
    } else {
        return "Boa noite!";
    }
}

const saudacao = determinarSaudacao();

// Retorna os dados para serem usados nos prÃ³ximos nÃ³s
